sys_script_include.script:
var GetUserInfo = Class.create();
GetUserInfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	//comments

	getUserDetails: function(){
		var obj = {};
			var userRecord = new GlideRecord("sys_user");
			if(userRecord.get(this.getParameter('sysparm_userID'))){

				obj.name = userRecord.getDisplayValue('name');
				obj.title = userRecord.getValue('title');
				obj.location = userRecord.getDisplayValue('location');
				obj.department = userRecord.getDisplayValue('department');
				obj.dm = userRecord.getValue('u_department_manager');

				//Reference Field will return sys_id. Use getDisplayValue() to return display value user_name=AFruhling
				obj.phone = userRecord.getValue('phone');
				obj.email = userRecord.getValue('email');
				obj.name = userRecord.getDisplayValue('name');

			}

			return JSON.stringify(obj);
		},


		type: 'GetUserInfo'
	});
